template_files.content:
  

proxy_http_version 1.1;
proxy_buffering off;



server {
    listen       5222 ssl;
    server_name  _Engines_Builder(fqdn);

    ssl_certificate /etc/loolwsd/cert.pem;
    ssl_certificate_key  /etc/loolwsd/key.pem;

 # static files
    location ^~ /loleaflet {
        proxy_pass https://127.0.0.1:9980;
        proxy_set_header Host $http_host;
    }

    # WOPI discovery URL
    location ^~ /hosting/discovery {
        proxy_pass https://127.0.0.1:9980;
        proxy_set_header Host $http_host;
    }
rewrite "/lool/(.*)/ws$"      wss://127.0.0.1:9980/lool/$1/ws permanent;
    # Main websocket
    location ~ /lool/(.*)/ws$ {

        proxy_pass https://127.0.0.1:9980;
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection "Upgrade";
        proxy_set_header Host $http_host;
        proxy_read_timeout 36000s;
    }

    # Admin Console websocket
    location ^~ /lool/adminws {
        proxy_pass https://127.0.0.1:9980;
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection "Upgrade";
        proxy_set_header Host $http_host;
        proxy_read_timeout 36000s;
    }

    # download, presentation and image upload
    location ^~ /lool {
        proxy_pass https://127.0.0.1:9980;
        proxy_set_header Host $http_host;
    }
}
